import React, {Component} from 'react'
import './navbar'
import Navbar from './navbar'
import Web3 from 'web3';



class App extends Component {

    async  UNSAFE_componentWillUnmount() {
        await this.loadWeb3()
    }

    async loadWeb3() {
        // 윈도우에서 이더리움이 감지되면 window.ethereum이 활성화될때까지 대기
        if(window.ethereuem) {
            window.web3 = new Web3(window.ethereuem)
            await window.ethereuem.enable()
        }
        // 윈도우에서 wev3가 감지되면 currentProvider로 새 weㅠ3 인스턴스를 생성
        else if (window.web3 )
            {
                window.web3 = new Web3(window.web3.currentProvider)
            } 
        // 둘중 하나라도 충족x일시 경고창 출력    
        else {
            window.alert('Error: check your metamask')
        }
    }

    constructor(props) {
        super(props)
        this.state = {
            account : '0x0'
        }
    }

    render() {
        return (
            <div>
                <Navbar account={this.state.account} />
            <div>
               <h1></h1>
            </div>
            </div>
        )
    }
}

export default App;
